package prep;

public class OddorEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num = 7;
		
		if(num==0) {
			System.out.println("Number is zero:  "+num);
		}else if(num%2 ==0) {
			System.out.println("Number is even: "+num);
		}else {
			System.out.println("Number is odd: "+num);
		}

	}

}
